def extract_session_token_from_html(html):
    """Extract session token from checkout HTML page"""
    pattern = r'AAEB[A-Za-z0-9_-]+'
    match = re.search(pattern, html)
    if match:
        return match.group(0)
    return None

def extract_queue_token(response_json):
    """Extract queueToken from GraphQL response"""
    try:
        queue_token = response_json['data']['session']['negotiate']['result']['queueToken']
        return queue_token
    except (KeyError, TypeError) as e:
        print(f"  ERROR extracting queueToken: {e}")
        print(f"  Response keys: {response_json.keys() if isinstance(response_json, dict) else 'NOT A DICT'}")
        if 'errors' in response_json:
            print(f"  GraphQL Errors: {response_json['errors']}")
        return None

def extract_receipt_id(response_json):
    """Extract receipt ID from submit response"""
    try:
        return response_json['data']['submitForCompletion']['receipt']['id']
    except (KeyError, TypeError):
        return None

